// Referencias a elementos del DOM
const searchForm = document.getElementById('searchForm');
const dniInput = document.getElementById('dni');
const errorMessage = document.getElementById('errorMessage');
const resultsSection = document.getElementById('resultsSection');
const studentAvatar = document.getElementById('studentAvatar');
const studentName = document.getElementById('studentName');
const studentDni = document.getElementById('studentDni');
const studentGrade = document.getElementById('studentGrade');
const learningStyles = document.getElementById('learningStyles');
const personality = document.getElementById('personality');
const vocationalOrientation = document.getElementById('vocationalOrientation');
const conclusionText = document.getElementById('conclusionText');
const careerList = document.getElementById('careerList');

// Función para generar iniciales del nombre
function getInitials(name) {
    return name.split(' ').map(word => word[0]).join('').toUpperCase();
}

// Función para cargar resultados del estudiante
function loadStudentResults(dni) {
    const student = studentsDatabase[dni];
    
    if (student) {
        errorMessage.style.display = 'none';
        
        // Actualizar información del estudiante
        studentAvatar.textContent = getInitials(student.name);
        studentName.textContent = student.name;
        studentDni.textContent = `DNI: ${dni}`;
        studentGrade.textContent = `Grado/Sección: ${student.grade}`;
        
        // Verificar si el alumno fue evaluado o no
        if (student.status === "no_evaluado") {
            // MOSTRAR MENSAJE ESPECIAL PARA NO EVALUADOS
            learningStyles.textContent = "No evaluado";
            personality.textContent = "No evaluado";
            vocationalOrientation.textContent = "No evaluado";
            conclusionText.textContent = student.message;
            
            // Mostrar mensaje de no disponible
            careerList.innerHTML = '';
            const noDataItem = document.createElement('div');
            noDataItem.className = 'career-item no-data';
            noDataItem.textContent = 'Información no disponible';
            careerList.appendChild(noDataItem);
        } else {
            // MOSTRAR INFORMACIÓN NORMAL PARA EVALUADOS
            learningStyles.textContent = student.learningStyles;
            personality.textContent = student.personality;
            vocationalOrientation.textContent = student.vocationalOrientation;
            conclusionText.textContent = student.conclusionText;
            
            // Cargar carreras
            careerList.innerHTML = '';
            student.careers.forEach(career => {
                const careerItem = document.createElement('div');
                careerItem.className = 'career-item';
                careerItem.textContent = career;
                careerList.appendChild(careerItem);
            });
        }
        
        // Mostrar resultados
        resultsSection.style.display = 'block';
        resultsSection.scrollIntoView({ behavior: 'smooth' });
    } else {
        errorMessage.textContent = "DNI no encontrado. Verifica el número e intenta nuevamente.";
        errorMessage.style.display = 'block';
        resultsSection.style.display = 'none';
    }
}

// Manejar envío del formulario
searchForm.addEventListener('submit', function(e) {
    e.preventDefault();
    
    const dni = dniInput.value.trim();
    
    if (dni.length === 8 && !isNaN(dni)) {
        loadStudentResults(dni);
    } else {
        errorMessage.textContent = "Por favor, ingresa un DNI válido de 8 dígitos.";
        errorMessage.style.display = 'block';
        resultsSection.style.display = 'none';
    }
});

// Validación en tiempo real del DNI
dniInput.addEventListener('input', function() {
    this.value = this.value.replace(/\D/g, '');
    
    if (this.value.length > 8) {
        this.value = this.value.slice(0, 8);
    }
    
    errorMessage.style.display = 'none';
});